import React from 'react'
import Stack from '@mui/material/Stack';
import { Gauge } from '@mui/x-charts/Gauge';
import {useState, useEffect} from 'react'
import axios from 'axios'
import { BarChart } from '@mui/x-charts/BarChart';

const MaxUV = () => {
    let [maxUV,setmaxUV] = useState(null)
    let [currentUV, setCurrentUV] = useState(null)
    useEffect(()=>{
    axios.get('https://api.open-meteo.com/v1/forecast?latitude=32.77&longitude=-96.79&daily=uv_index_max').then((response) => {
        setmaxUV(response.data)
        console.log(response)
        }
    )
    axios.get('https://api.open-meteo.com/v1/forecast?latitude=32.814&longitude=-96.9489&hourly=uv_index&timezone=America%2FChicago&forecast_hours=1').then((response) => {
        setCurrentUV(response.data)
        console.log(response.data)
        }
    )
},[])
    return (
    <div>
    <Stack direction={{ xs: 'column', md: 'row' }} spacing={{ xs: 1, md: 3 }}>
      <Gauge width={100} height={100} value={6} valueMin={0} valueMax={11} />
    </Stack>
    <BarChart
      xAxis={[{ scaleType: 'band', data: maxUV? maxUV.daily.time: [] }]}
      series={[{ data: maxUV? maxUV.daily.uv_index_max: [] }]}
      width={900}
      height={300}  />
    </div>
    )
}
export default MaxUV
