import axios from 'axios'
import React from 'react'
import {useState,useEffect} from 'react'
import { BarChart } from '@mui/x-charts/BarChart';

const UVPrediction = () => {
    let [UVPred, setUVPred] = useState(null)
    let [timeV, setTime] = useState(null)
        useEffect(()=>{
            axios.get('https://api.open-meteo.com/v1/forecast?latitude=32.814&longitude=-96.9489&hourly=uv_index&timezone=America%2FChicago').then((response)=>{
                setUVPred(response.data.hourly.uv_index)
                setTime(response.data.hourly.time)
            })
        },[])
    return(
        <div>
            <BarChart
      xAxis={[{ scaleType: 'band', data: UVPred? Array.from({ length: 24 }, (v, i) => i): [] }]}
      series={[{ data: UVPred? UVPred.slice(0,24): [] }]}
      width={300}
      height={300}  />
    </div>
    )
}
export default UVPrediction
